import React, { Component, useEffect} from 'react';

import { useRef } from 'react';
import { AiFillDelete } from "react-icons/ai";
import { GrUpdate } from "react-icons/gr";
import { Link } from 'react-router-dom';
import { RiAddBoxFill } from "react-icons/ri";
import {useSelector, useDispatch} from 'react-redux';
import { BsFillSkipBackwardFill, BsFillSkipForwardFill } from "react-icons/bs";

import Select from 'react-select'
import {CheckApiLogin, LogOutFun, GetUsername} from '../redux/reduxLogin.js';


import {useState} from 'react';
import {ShowContact} from '../redux/reduxContact.js';
import $ from 'jquery'; 
var Recaptcha = require('react-recaptcha');

function Contact() {
  const linkProfile = useRef();
  const password = useRef();
  const [foodState, setFoodState] = useState("dumpling");
  const author = useRef();
  const colourOptions = [
    { value: "red", label: "Red" },
    { value: "green", label: "Green" },
    { value: "blue", label: "Blue" }
  ];

  const colourStyles = {
    option: (styles, { data, isDisabled, isFocused, isSelected }) => {
      // const color = chroma(data.color);
      console.log({ data, isDisabled, isFocused, isSelected });
      return {
        ...styles,
        backgroundColor: isFocused ? "rgb(248, 249, 252)" : null,
        color: "#333333"
      };
    }
  };
  const optionsasd = [
    { label: null },
    { label: null },
    { label: '1' },
    { label: '2' },
    { label: '3' },
    { label: '4' },
    { label: '5' },
    { label: '6' },
    { label: '7' },
    { label: '8' },
    { label: '9' },
    { label: '10' },
    { label: '11' },
    { label: '12' },
    { label: '13' },
    { label: '14' },
    { label: '15' },
    { label: '16' },
    { label: '17' },
    { label: '18' },
    { label: '19' },
    { label: '20' }
  ]
    return (
      <>
      <body style={{ backgroundColor: `#f8f9fc`, fontFamily: 'ui-monospace'}}>
      <div class="container" >
<div class="row justify-content-center"  >

    <div class="col-xl-10 col-lg-12 col-md-9" >

        <div class="card o-hidden border-0 shadow-lg my-5" >
            <div class="card-body p-1"   style={{ border: `1px dashed #4e73df`}} >
            <div class="row">
            <div class="col-lg-12">
                                <div class="p-5"><div class="text-center">

                                        <h1 class="h2 usernameLogin1">CRAWL DATA APPLICATION</h1> <hr/>
                                    </div></div></div></div>
        <div class="row">
                            <div class="col-lg-1"></div>
                            <div class="col-lg-5">
                                    <form class="user">
                                        <div class="form-group">
                                            <strong>Đường dẫn profile (<span style={{color: 'red'}}>*</span>):</strong>
                                        </div>
                                        <div class="form-group">
                                            <input type="text" class="form-control" placeholder="Link profile..." ref={linkProfile} />
                                        </div>
                                        <div class="form-group">
                                            <strong>Số lượng bài đăng:</strong>
                                        </div>
                                        <div class="form-group">
                                            <Select options={optionsasd} ref={author} placeholder='Choose number post...'/>
                                        </div>
                                        <div class="form-group">
                                            <strong>Thời gian kể từ:</strong>
                                        </div>
                                        <div class="form-group">
                                          <input type="date" class="form-control form-control-user" id="exampleInputEmail" aria-describedby="emailHelp" data-date="" data-date-format="DD MMMM YYYY"/>
                                        </div>
                                    </form>
                            </div>
                            <div class="col-lg-4" style={{marginLeft : '27px'}}>
                            <form class="user">
                                        <div class="form-group">
                                            <strong  style={{paddingLeft : '27px'}}>Kiểu: (<span style={{color: 'red'}}>*</span>):</strong>
                                        </div>
                                        <div class="form-group">
                                            <Select
                                                defaultValue={colourOptions[1]}
                                                label="Single select"
                                                options={colourOptions}
                                                styles={colourStyles}
                                            />
                                        </div>
                                        <Select
                                            defaultValue={colourOptions[1]}
                                            label="Single select"
                                            options={colourOptions}
                                            styles={colourStyles}
                                        />
                                    </form>
                            </div>

                        </div>
                        <br/><br/>
                                    <div class="row"><div class="col-lg-3"></div><div class="col-lg-5"><div class="text-center" style={{marginLeft: '50px'}}>
                                        <Recaptcha
                                          sitekey="6LexfIEeAAAAALoxq3Voba6IghcOck3vscF8Q9yG"
                                          render="exp"

                                        />
                                    </div></div></div>
                        <div class="row">
                        <div class="col-lg-1"></div>
                        <div class="col-lg-10">
                                <div class="p-3"><hr/>
                            <a href="#" class="btn btn-primary btn-user btn-block" onClick={() => {ShowContact(linkProfile.current.value, author)}}>
                                            <strong style={{height: '42px', paddingTop: '7px', fontSize: 'large'}}>START CRAWL</strong><br/>
                                        </a></div>
                                        </div></div><br/><div class="row"><div class="col-lg-1"></div></div>
                        </div>
                                </div>
                            </div>
                        </div>
                        </div>
                        </body>
      </>
  );  
}



export default Contact;
