import React, { Component, useEffect } from 'react';
import { AiFillDelete, AiOutlineCarryOut } from "react-icons/ai";
import { GrUpdate} from "react-icons/gr";
import { Link } from 'react-router-dom';
import { RiAddBoxFill } from "react-icons/ri";
import { BsFillSkipBackwardFill, BsFillSkipForwardFill } from "react-icons/bs";
import { GrChapterAdd} from "react-icons/gr";

import {useState} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {ShowComments, GetCommentSize, ChangeListCommentByPageIndex, delFunc} from '../redux/reduxComment.js';
import {ShowUsers, GetUserSize, ChangeListUserByPageIndex, DeletedUserFunc} from '../redux/reduxUser.js';
import $ from 'jquery'; 

export function Comment() {
  $(function(){
    $(".colorMenu").removeClass("colorMenuActive");
    $("#customS4S1").removeClass("colorMenuActive1");
    $("#customS4S2").removeClass("colorMenuActive1");
    if (localStorage.getItem('statusColorHome') == 1) {
        $("#colorMenu2").addClass("colorMenuActive");
    } else {
        $("#colorMenu2").removeClass("colorMenuActive");
    }
    $("#colorMenu2").addClass("colorMenuActive");
    $("#customS4S1").addClass("colorMenuActive1");

  });

  const dispatch = useDispatch(); 
  let [user, setUser] = useState({});
  useEffect(() => {
    setUser(dispatch(ShowUsers()));
  }, user)

  const listUserSelector = useSelector(state => state);
  const listUser = Array.from(listUserSelector);

  function getUser() {
    return (
      listUser.map((item, index) => {
        var bookName = "";
        if (item.books != null) {
            for (var i = 0; i < item.books.length; i++) {
            if (i != item.books.length) {
                bookName += "["+item.books[i].name +"] ";
            }
            }
        }
        if (item.username !== "admin") {
          return (
                  <tr key={index}>
                  <td>{item.id}</td>
                  <td>{item.username}</td>
                  <td>{item.password}</td>
                  <td>{bookName}</td>
                  <td>{item.role}</td>
                  <td>
                    <Link to={`/userChange/${item.id}`} ><button class="updatedBtn"><GrUpdate/></button></Link>
                    <button class="deletedBtn" onClick={() => { if (window.confirm('Are you sure you wish to delete this item?'))  DeletedUserFunc(item.id) }}><AiFillDelete/></button>
                  </td>
                  </tr>
                 )
        } else {
          return (
            <tr key={index} style={{color:'red'}}>
            <td>{item.id}</td>
            <td>{item.username}</td>
            <td>{item.password}</td>
            <td>...</td>
            <td>{item.role}</td>
            <td>...
            </td>
            </tr>
           )
        }
      })
    )
  }

  return (
    <>
    <body>
        <div class="container-fluid">
            < div class="row">
                <div class="col-lg-3"></div>
                <div class="col-lg-6 text-center">
                    <br/><span class="usernameLogin2">Scenarios</span><br/>
                    <span class="cusText">This table show script information in project. <Link to="#"><GrChapterAdd style={{color: 'red'}}/></Link></span>
                </div>
            </div>
            <div class="card shadow mb-4" style={{marginTop: '20px'}}>
                <div class="card-header py-3">
                    <h6 class="m-0 font-weight-bold text-primary">Scenarios Table</h6>
                </div>
                <div class="card-body">
                    <div class="table-responsive">
                        <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Name</th>
                                    <th>Description</th>
                                    <th>Script</th>
                                    <th>Create Date</th>
                                    <th>Last Modified By</th>
                                    <th>Type</th>
                                    <th>Functions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {getUser()}
                            </tbody>
                        </table>
                    </div>
                    <div class="row">
                        <div class="col-lg-12 text-right">
                            <button class="phanTrangBtn">1</button>
                            <button class="phanTrangBtn">2</button>
                            <button class="phanTrangBtn">3</button>
                            <button class="phanTrangBtn">4</button>
                            <button class="phanTrangBtn">5</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </body>
    </>
  );  
}


export default Comment;
