import React, { Component, useEffect } from 'react';
import { AiFillDelete } from "react-icons/ai";
import { GrUpdate } from "react-icons/gr";
import { Link } from 'react-router-dom';
import { RiAddBoxFill } from "react-icons/ri";
import { HiUserAdd } from "react-icons/hi";
import { BsFillSkipBackwardFill, BsFillSkipForwardFill } from "react-icons/bs";

import {useState} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {ShowAuthors, GetAuthorSize, ChangeListAuthorByPageIndex, DeletedAuthorFunc} from '../redux/reduxAuthor.js';
import $ from 'jquery';
import {PhotoshopPicker, SketchPicker} from 'react-color';

function Author() {

  return (
    <>
      <body>
      <br/><br/>
      <div class="row">
      <div class="col-lg-10"><h1 class="h2 usernameLogin1">CRAWL DATA APPLICATION</h1> <hr/></div>
      <div class="switch">
        <input id="language-toggle" class="check-toggle check-toggle-round-flat" type="checkbox" />
        <label for="language-toggle"></label>
        <span class="on">VN</span>
        <span class="off">EN</span>
      </div>
      <br/><br/><br/><br/><br/><br/>
      </div>
      <div class="row">
              <div class="col-lg-2"></div>
              <div class="col-lg-3">
              <div class="text-center"><PhotoshopPicker/></div>
              </div>
              <div class="col-lg-1"></div>
              <div class="col-lg-2"></div>
              <div class="col-lg-1">
              <div class="text-center"><SketchPicker/></div>
              </div>
              <div class="col-lg-3">
              <div class="text-center">
              </div>
              </div>
              </div>
      </body>
    </>  
  );
}


export default Author;
