import React, { Component, useEffect } from 'react';
import { AiFillDelete, AiOutlineCarryOut } from "react-icons/ai";
import { GrUpdate} from "react-icons/gr";
import { Link } from 'react-router-dom';
import { RiAddBoxFill } from "react-icons/ri";
import { BsFillSkipBackwardFill, BsFillSkipForwardFill } from "react-icons/bs";
import { GrChapterAdd} from "react-icons/gr";

import {useState} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {ShowComments, GetCommentSize, ChangeListCommentByPageIndex, delFunc} from '../redux/reduxComment.js';
import {ShowUsers, GetUserSize, ChangeListUserByPageIndex, DeletedUserFunc} from '../redux/reduxUser.js';
import $ from 'jquery'; 

export function Comment() {
  $(function(){
    $(".colorMenu").removeClass("colorMenuActive");
    $("#customS4S1").removeClass("colorMenuActive1");
    $("#customS4S2").removeClass("colorMenuActive1");
    if (localStorage.getItem('statusColorHome') == 1) {
        $("#colorMenu2").addClass("colorMenuActive");
    } else {
        $("#colorMenu2").removeClass("colorMenuActive");
    }
    $("#colorMenu2").addClass("colorMenuActive");
    $("#customS4S1").addClass("colorMenuActive1");

  });

  const dispatch = useDispatch(); 
  let [comment, setComment] = useState({});
  useEffect(() => {
    setComment(dispatch(ShowComments()));
  }, comment)

  const listCommentSelector = useSelector(state => state);
  const listComment = Array.from(listCommentSelector);

  function getComment() {
    return (
      listComment.map((item, index) => {
        //item.user.usernameconsole.log(item.user.username);{item.user.username}{item.book.name}
        //var arrUser = [];
        //for (var i = 0; i < item.user.length; i++) {
        //  
        //}
        //console.log(item.user.length);
          var username = "";
          var bookname = "";
          if (item.book != null){
            bookname = item.book.name;
          }
          if (item.user != null) {
            username = item.user.username;
          }
          return (
          <tr key={index}>
            <td>{item.id}</td>
            <td>{item.comment}</td>
            <td>{bookname}</td>
            <td>{username}</td>
            <td>
              <button class="deletedBtn" onClick={() => { if (window.confirm('Are you sure you wish to delete this item?'))  delFunc(item.id) } }><AiFillDelete /></button>
            </td>
          </tr>
        )
      })
    )
  }

  function paginationFunc() {
    var arr = [];
    var index = GetCommentSize();
    var numberOfPages = Math.floor(index / 4);
    for (var i = 1 ; i <= numberOfPages; i++) {
      var index = i;
      arr.push(i);
    }

    return (
      arr.map((item, index) => {
        var idStr = "index" + item;
        return (
          <button class="phanTrangBtn" id={idStr} onClick={() => {dispatch(ChangeListCommentByPageIndex(item))}}>{item}</button>
        )
      })
    )
  }

  return (
    <>
    <body>
        <div class="container-fluid">
            < div class="row">
                <div class="col-lg-3"></div>
                <div class="col-lg-6 text-center">
                    <br/><span class="usernameLogin2">Scenarios</span><br/>
                    <span class="cusText">This table show script information in project. <Link to="#"><GrChapterAdd style={{color: 'red'}}/></Link></span>
                </div>
            </div>
            <div class="card shadow mb-4" style={{marginTop: '20px'}}>
                <div class="card-header py-3">
                    <h6 class="m-0 font-weight-bold text-primary">Scenarios Table</h6>
                </div>
                <div class="card-body">
                    <div class="table-responsive">
                        <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                            <thead>
                                <tr>
                                    <th style={{width:'5%'}}>ID</th>
                                    <th style={{width:'10%'}}>Username</th>
                                    <th style={{width:'15%'}}>Password</th>
                                    <th style={{width:'15%'}}>Last Login</th>
                                    <th style={{width:'15%'}}>Create Date</th>
                                    <th style={{width:'10%'}}>Create By</th>
                                    <th style={{width:'20%'}}>Functions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {getComment()}
                            </tbody>
                        </table>
                    </div>
                    <div class="row">
                        <div class="col-lg-12 text-right">
                            {paginationFunc()}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </body>
    </>
  );  
}


export default Comment;
