import React, { Component, useEffect } from 'react';
import { AiFillDelete } from "react-icons/ai";
import { GrUpdate } from "react-icons/gr";
import { Link } from 'react-router-dom';
import { RiAddBoxFill } from "react-icons/ri";
import { HiUserAdd } from "react-icons/hi";
import { BsFillSkipBackwardFill, BsFillSkipForwardFill } from "react-icons/bs";

import {useState} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {ShowUsers, GetUserSize, ChangeListUserByPageIndex, DeletedUserFunc} from '../redux/reduxUser.js';
import $ from 'jquery'; 


function User() {

  return (
    <>
      <body class="bodyMe">
        <video class="bg-video" playsinline="playsinline" autoplay="autoplay" muted="muted" loop="loop"><source src="./assets/mp4/bg.mp4" type="video/mp4" /></video>
        <div class="masthead">
            <div class="masthead-content text-white">
                <div class="container-fluid px-4 px-lg-0">
                    <h1 class="fst-italic lh-1 mb-4">Crawl Data</h1>
                    <p class="mb-5">Chúng tôi đang làm việc chăm chỉ để hoàn thiện ứng dụng mỗi ngày.
                        <br/><br/> Hãy đăng ký bên dưới để nhận các bản cập nhật và được thông báo khi chúng tôi cập nhật nhé!</p>
                    <form id="contactForm" data-sb-form-api-token="API_TOKEN">
                        <div class="row input-group-newsletter">
                            <div class="col"><input class="form-control" id="email" type="email" placeholder="Enter email address..." aria-label="Enter email address..." data-sb-validations="required,email" /></div>
                            <div class="col-auto"><button class="btn btn-primary disabled" id="submitButton" type="submit">Thông báo!</button></div>
                        </div>
                        <div class="invalid-feedback mt-2" data-sb-feedback="email:required">Email không được để trống.</div>
                        <div class="invalid-feedback mt-2" data-sb-feedback="email:email">Email không hợp lệ.</div>
                        <div class="d-none" id="submitSuccessMessage">
                            <div class="text-center mb-3 mt-2">
                                <div class="fw-bolder">Liên hệ thành công!</div>
                                Cảm ơn bạn đã liên hệ với chúng tôi
                            </div>
                        </div>
                        <div class="d-none" id="submitErrorMessage"><div class="text-center text-danger mb-3 mt-2">Error sending message!</div></div>
                    </form>
                </div>
            </div>
        </div>
    </body>
    </>
  );
}


export default User;
